*/

import { useState } from "react";

// @mui material components
import Container from "@mui/material/Container";
import Grid from "@mui/material/Grid";
import Modal from "@mui/material/Modal";
import Button from "@mui/material/Button";
import Divider from "@mui/material/Divider";
import Slide from "@mui/material/Slide";

// @mui icons
import CloseIcon from "@mui/icons-material/Close";

// Material Kit 2 React components
import MKBox from "components/MKBox";
import MKButton from "components/MKButton";
import MKTypography from "components/MKTypography";

// Material Kit 2 React components
import TransparentBlogCard from "examples/Cards/BlogCards/TransparentBlogCard";
import ImageCard from "examples/Cards/BlogCards/ImageCard";

// Face Type Images
// import M20 from "assets/images/faces/20대 남성.png";
// import M30 from "assets/images/faces/30대 남성.png";
// import M40 from "assets/images/faces/40대 남성.png";
// import M50 from "assets/images/faces/50대 남성.png";
import W20 from "assets/images/faces/20w.png";
// import W30 from "assets/images/faces/30대 여성.png";
// import W40 from "assets/images/faces/40대 여성.png";
// import W50 from "assets/images/faces/50대 여성.png";

// Eye Type Images
import E1 from "assets/images/eyes/milk.png";
import E2 from "assets/images/eyes/clio_sharp.png";
import E3 from "assets/images/eyes/clio_kill.png";

// Base Type Images
import B1 from "assets/images/bases/bglow.png";
import B2 from "assets/images/bases/fresh.png";
import B3 from "assets/images/bases/killcover.png";

// Lip Type Images
import L1 from "assets/images/lips/bbia.png";
import L2 from "assets/images/lips/peripera.png";
import L3 from "assets/images/lips/mac.png";

const eyes = [
  [E1, "밀크터치 올데이 롱앤컬 마스카라"],
  [E2, "클리오 샤프, 쏘 심플 워터프루프 펜슬라이너"],
  [E3, "클리오 킬래쉬 수퍼프루프마스카라"],
];

const eyeColors = ["블랙", "브라운", "다크 브라운", "카카오 브라운"];

const bases = [
  [B1, "에스쁘아 비글로우 파운데이션"],
  [B2, "에스쁘아 프레쉬 세팅 파우더"],
  [B3, "클리오 킬커버 광채 파운데이션"],
];

const baseColors = ["바닐라", "아이보리", "페탈", "베이지"];

const lips = [
  [L1, "삐아 라스트 벨벳 립 틴트 14종"],
  [L2, "페리페라 잉크 무드 매트 틴트"],
  [L3, "MAC 립스틱"],
];

const lipColors = ["레드", "오렌지", "핑크", "버건디"];

function Select({ setStep }) {
  const [show, setShow] = useState(Array(9).fill(false));

  const toggleModal = (i) => {
    const newShow = Array(9).fill(false);
    newShow[i] = !show[i];
    setShow(newShow);
  };

  (() => {
    eyeColors.push(null);
    baseColors.push(null);
    lipColors.push(null);
  })();

  const onClickNext = (e) => {
    e.preventDefault();
    setStep(3);
  };

  // const onClickCard = (e) => {
  //   e.preventDefault();
  //   e.target.closest(".unSelected").classList.add("selected");
  // };

  return (
    <MKBox component="section" py={3}>
      <Container xs={12}>
        <Grid
          container
          item
          xs={12}
          lg={6}
          justifyContent="center"
          alignItems="center"
          flexDirection="column"
          sx={{ mx: "auto", textAlign: "center", marginBottom: "10px" }}
        >
          <MKTypography variant="h3" verticalAlign="middle" mb={6}>
            여기에 내용을 적어주세요. 스타일을 변경하고 싶으시다면 sx 안에 CSS 를 작성해주세요.
            variant=h1, h2, h3, ..., h6
          </MKTypography>
        </Grid>
        <Grid container mt={0.1} spacing={2} xs={12} direction="row">
          <Grid item xs={4} sm={6} lg={5}>
            <ImageCard image={W20} title="20대 여성" />
          </Grid>

          <Grid xs={1} />

          <Grid
            container
            xs={6}
            spacing={1}
            sx={{
              "& .selected": {
                borderRadius: "10px",
                boxShadow: "0px 2.5px 5px #c4c4c4",
              },
            }}
          >
            {/* Eye */}
            <Grid container item xs={12} spacing={3}>
              <MKTypography variant="h5">Eyes</MKTypography>
              <Grid container item xs={12} spacing={3}>
                {eyes.map((eye, i) => (
                  <>
                    <Grid
                      item
                      xs={6}
                      sm={6}
                      lg={4}
                      onClick={() => {
                        toggleModal(i);
                      }}
                      sx={{
                        padding: "10px 20px 0px 0px",
                      }}
                    >
                      <TransparentBlogCard image={eye[0]} title={eye[1]} />
                    </Grid>
                    <Modal
                      open={show[i]}
                      onClose={() => {
                        toggleModal(i);
                      }}
                      sx={{
                        display: "grid",
                        placeItems: "center",
                      }}
                    >
                      <Slide direction="down" in={show[i]} timeout={500}>
                        <MKBox
                          position="relative"
                          width="500px"
                          display="flex"
                          flexDirection="column"
                          borderRadius="xl"
                          bgColor="white"
                          shadow="xl"
                          sx={{
                            "&:focus": {
                              outline: "none",
                            },
                          }}
                        >
                          <MKBox
                            display="flex"
                            alginItems="center"
                            justifyContent="space-between"
                            p={2}
                          >
                            <MKTypography variant="h5">Your modal title</MKTypography>
                            <CloseIcon
                              fontSize="medium"
                              sx={{ cursor: "pointer" }}
                              onClick={toggleModal}
                            />
                          </MKBox>
                          <Divider sx={{ my: 0 }} />
                          <MKBox p={2}>
                            <MKTypography variant="body2" color="secondary" fontWeight="regular">
                              Society has put up so many boundaries, so many limitations on
                              what&apos;s right and wrong that it&apos;s almost impossible to get a
                              pure thought out.
                              <br />
                            </MKTypography>
                          </MKBox>
                          <Divider sx={{ my: 0 }} />
                          <MKBox display="flex" justifyContent="space-between" p={1.5}>
                            <MKButton variant="gradient" color="dark" onClick={toggleModal}>
                              close
                            </MKButton>
                            <MKButton variant="gradient" color="info">
                              save changes
                            </MKButton>
                          </MKBox>
                        </MKBox>
                      </Slide>
                    </Modal>
                  </>
                ))}
              </Grid>
            </Grid>

            {/* Base */}
            <Grid container item xs={12} spacing={3}>
              <MKTypography variant="h5" mt={4}>
                Bases
              </MKTypography>
              <Grid container item xs={12} spacing={3}>
                {bases.map((base, i) => (
                  <>
                    <Grid
                      item
                      xs={6}
                      sm={6}
                      lg={4}
                      onClick={() => {
                        toggleModal(i);
                      }}
                      sx={{
                        padding: "10px 25px 0px 0px",
                      }}
                    >
                      <TransparentBlogCard image={base[0]} title={base[1]} />
                    </Grid>
                    <Modal
                      open={show[i]}
                      onClose={() => {
                        toggleModal(i);
                      }}
                      sx={{
                        display: "grid",
                        placeItems: "center",
                      }}
                    >
                      <Slide direction="down" in={show[i]} timeout={500}>
                        <MKBox
                          position="relative"
                          width="500px"
                          display="flex"
                          flexDirection="column"
                          borderRadius="xl"
                          bgColor="white"
                          shadow="xl"
                          sx={{
                            "&:focus": {
                              outline: "none",
                            },
                          }}
                        >
                          <MKBox
                            display="flex"
                            alginItems="center"
                            justifyContent="space-between"
                            p={2}
                          >
                            <MKTypography variant="h5">Your modal title</MKTypography>
                            <CloseIcon
                              fontSize="medium"
                              sx={{ cursor: "pointer" }}
                              onClick={toggleModal}
                            />
                          </MKBox>
                          <Divider sx={{ my: 0 }} />
                          <MKBox p={2}>
                            <MKTypography variant="body2" color="secondary" fontWeight="regular">
                              Society has put up so many boundaries, so many limitations on
                              what&apos;s right and wrong that it&apos;s almost impossible to get a
                              pure thought out.
                              <br />
                            </MKTypography>
                          </MKBox>
                          <Divider sx={{ my: 0 }} />
                          <MKBox display="flex" justifyContent="space-between" p={1.5}>
                            <MKButton variant="gradient" color="dark" onClick={toggleModal}>
                              close
                            </MKButton>
                            <MKButton variant="gradient" color="info">
                              save changes
                            </MKButton>
                          </MKBox>
                        </MKBox>
                      </Slide>
                    </Modal>
                  </>
                ))}
              </Grid>
            </Grid>

            {/* Lip */}
            <Grid container item xs={12} spacing={3}>
              <MKTypography variant="h5" mt={4}>
                Lips
              </MKTypography>
              <Grid container item xs={12} spacing={3}>
                {lips.map((lip, i) => (
                  <>
                    <Grid
                      item
                      xs={6}
                      sm={6}
                      lg={4}
                      onClick={() => {
                        toggleModal(i);
                      }}
                      sx={{
                        padding: "10px 25px 0px 0px",
                      }}
                    >
                      <TransparentBlogCard image={lip[0]} title={lip[1]} />
                    </Grid>
                    <Modal
                      open={show[i]}
                      onClose={() => {
                        toggleModal(i);
                      }}
                      sx={{
                        display: "grid",
                        placeItems: "center",
                      }}
                    >
                      <Slide direction="down" in={show[i]} timeout={500}>
                        <MKBox
                          position="relative"
                          width="500px"
                          display="flex"
                          flexDirection="column"
                          borderRadius="xl"
                          bgColor="white"
                          shadow="xl"
                          sx={{
                            "&:focus": {
                              outline: "none",
                            },
                          }}
                        >
                          <MKBox
                            display="flex"
                            alginItems="center"
                            justifyContent="space-between"
                            p={2}
                          >
                            <MKTypography variant="h5">Your modal title</MKTypography>
                            <CloseIcon
                              fontSize="medium"
                              sx={{ cursor: "pointer" }}
                              onClick={toggleModal}
                            />
                          </MKBox>
                          <Divider sx={{ my: 0 }} />
                          <MKBox p={2}>
                            <MKTypography variant="body2" color="secondary" fontWeight="regular">
                              Society has put up so many boundaries, so many limitations on
                              what&apos;s right and wrong that it&apos;s almost impossible to get a
                              pure thought out.
                              <br />
                            </MKTypography>
                          </MKBox>
                          <Divider sx={{ my: 0 }} />
                          <MKBox display="flex" justifyContent="space-between" p={1.5}>
                            <MKButton variant="gradient" color="dark" onClick={toggleModal}>
                              close
                            </MKButton>
                            <MKButton variant="gradient" color="info">
                              save changes
                            </MKButton>
                          </MKBox>
                        </MKBox>
                      </Slide>
                    </Modal>
                  </>
                ))}
              </Grid>
            </Grid>

            <Button
              size="large"
              variant="contained"
              color="info"
              sx={{ margin: "0 0 0 420px" }}
              onClick={onClickNext}
            >
              선택 완료
            </Button>
          </Grid>
        </Grid>
      </Container>
    </MKBox>
  );
}

Select.defaultProps = {
  setStep: () => {},
};

Select.propTypes = {
  setStep: () => {},
};

export default Select;
